<?phpnamespace App\EventListener;use Doctrine\ORM\EntityManagerInterface;use Oneup\UploaderBundle\Event\PostPersistEvent;use Oneup\UploaderBundle\Uploader\Response\ResponseInterface;class UploadListener{    /**     * @var EntityManagerInterface     */    private $om;    public function __construct(EntityManagerInterface $om)    {        $this->om = $om;    }    public function onUpload(PostPersistEvent $event) : ResponseInterface    {        $response = $event->getResponse();        $response['fileName'] = $event->getFile()->getBasename();        $response['success'] = true;        return $response;    }}